import R from 'ramda';
import {
    ADD_GOOD_TO_BASKET,
    DELETE_FROM_BASKET
} from 'actionType';

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch(type) {
        case ADD_GOOD_TO_BASKET :
            return R.append(payload, state);
        case DELETE_FROM_BASKET :
            return R.map(good => {
                for(let key in good) {
                    if(key === payload) {
                        delete good[key]
                    }
                }
                return good
            })(state);
        default :
            return state;
    }
}