import R from 'ramda';
import {
    FETCH_CATEGORIES_SUCCESS
} from 'actionType';

const initialState = {
    ids : []
};

export default (state = initialState, {type, payload}) => {
    switch(type) {
        case FETCH_CATEGORIES_SUCCESS :
            return R.merge(state, { ids : payload });
        default :
            return state;
    }
}